INSERT INTO pwnymon VALUES(1,'bulbasaur',replace('A strange seed was\nplanted on its\nback at birth.The plant sprouts\nand grows with\nthis POKéMON.','\n',char(10)),'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png');
