INSERT INTO pwnymon VALUES(4,'charmander',replace('Obviously prefers\nhot places. When\nit rains, steamis said to spout\nfrom the tip of\nits tail.','\n',char(10)),'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/4.png');
