INSERT INTO pwnymon VALUES(12,'butterfree',replace('In battle, it\nflaps its wings\nat high speed torelease highly\ntoxic dust into\nthe air.','\n',char(10)),'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/12.png');
