INSERT INTO pwnymon VALUES(24,'arbok',replace('It is rumored that\nthe ferocious\nwarning markingson its belly\ndiffer from area\nto area.','\n',char(10)),'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/24.png');
