INSERT INTO pwnymon VALUES(58,'growlithe',replace('Very protective\nof its territory.\nIt will bark andbite to repel\nintruders from\nits space.','\n',char(10)),'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/58.png');
